import React, { useState } from "react";

export const PaymentForm = () => {
  const [stateObject, setStateObject] = useState({
    name: "",
    price: 0,
    date: new Date(),
  });
  const submitHandler = (event) => {
    event.preventDefault();
  };

  const changeHandler = (event) => {
    setStateObject((prevState) => {
      console.log(event.target.id, event.target.value);
      prevState[event.target.id] = event.target.value;
      return { ...prevState };
    });
  };
  const changeDateHandler = (event) => {
    setStateObject((prevState) => {
      prevState[event.target.id] = new Date(event.target.value);
      console.log(prevState);
      return { ...prevState };
    });
  };
  return (
    <form onSubmit={submitHandler}>
      <input
        type="text"
        id="name"
        value={stateObject.price}
        onChange={changeHandler}
      />
      <input
        type="number"
        id="price"
        value={stateObject.name}
        onChange={changeHandler}
      />
      <input
        type="date"
        id="date"
        value={stateObject.date.toISOString().split("T")[0]}
        onChange={changeHandler}
      />
      <input type="submit" value="" />
    </form>
  );
};
